mapper.update:
update activity_source set
		sort =#{sort}
where id=#{id}

mapper.select:
SELECT * FROM ( select ac.*,pu.realname from activity_source ac LEFT JOIN pmph_user pu ON ac.uploader_id = pu.id where 1=1 and ac.is_deleted=0
and (ac.source_name like concat('%',#{parameter.sourceName},'%'))
order by ac.sort asc ) a, ( select count(*) count from activity_source ac LEFT JOIN pmph_user pu ON ac.uploader_id = pu.id where 1=1 and ac.is_deleted=0
and (ac.source_name like concat('%',#{parameter.sourceName},'%'))
)b
limit #{start},#{pageSize}

SELECT * FROM ( select ac.id,ac.source_name,ac.uploader_id,ac.gmt_upload,ac.file_id,ac.is_deleted,pu.realname,a.sort from activity_source_chain a left join activity_source ac on a.activity_source_id=ac.id LEFT JOIN pmph_user pu ON ac.uploader_id = pu.id where 1=1 and ac.is_deleted=0
and a.activity_id=#{parameter.activityId}
and (ac.source_name like concat('%',#{parameter.sourceName},'%'))
order by a.sort asc ) a, ( select count(*) count from activity_source_chain a left join activity_source ac on a.activity_source_id=ac.id LEFT JOIN pmph_user pu ON ac.uploader_id = pu.id where 1=1 and ac.is_deleted=0
and a.activity_id=#{parameter.activityId}
and (ac.source_name like concat('%',#{parameter.sourceName},'%'))
)b
limit #{start},#{pageSize}

mapper.update:
	update activity_source_chain set
		sort =#{sort}
where activity_id=#{activityId}
and activity_source_id=#{activitySourceId}

mapper.select:
SELECT * FROM
(
select *
from activity_source_chain ac
where ac.sort  < #{sort}
and ac.activity_id=#{activityId}
order by sort desc
)a
limit 0,1

SELECT * FROM
(
select *
from activity_source_chain ac
where ac.sort > #{sort}
and ac.activity_id=#{activityId}
order by sort asc
)a
limit 0,1
