mapper.select:
select count(a.id) from book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = a.book_id LEFT JOIN writer_user d on d.id = a.user_id LEFT JOIN pmph_user p on p.id=c.editor_id where b.id is not null and !a.is_deleted
and c.editor_id = #{editorId}
and b.bookname like CONCAT('%',#{bookname},'%')
and is_editor_replied
and a.result = #{result}
and (is_editor_replied and a.result = #{result} )

select DISTINCT a.id, b.bookname, b.author, b.publish_date, b.image_url , a.page, a.line , a.content , concat('/file/download/',a.attachment) attachment, a.attachment_name, d.realname, a.gmt_create , a.is_author_replied, a.author_reply , a.is_editor_handling , a.is_editor_replied , a.editor_reply, a.result, p.realname editorName from book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = a.book_id LEFT JOIN writer_user d on d.id = a.user_id LEFT JOIN pmph_user p on p.id=c.editor_id where b.id is not null and !a.is_deleted
and c.editor_id = #{editorId}
and b.bookname like CONCAT('%',#{bookname},'%')
and is_editor_replied
and a.result = #{result}
and (is_editor_replied and a.result = #{result} )
ORDER BY a.gmt_create desc limit #{start} ,#{pageSize}

SELECT count(DISTINCT a.id) FROM book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = b.id LEFT JOIN pmph_user d on d.id = c.editor_id LEFT JOIN pmph_department e on e.id = d.department_id LEFT JOIN material_type f on f.id = b.type where !a.is_deleted and b.id is not null
and b.bookname like CONCAT('%',#{bookname},'%')
and a.is_editor_replied = #{isEditorReplied}

SELECT a.id , b.bookname, e.dp_name , f.type_name , d.realname , g.realname dutyName, a.result , a.page, a.line, a.content, a.author_reply, a.is_editor_replied FROM book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = b.id LEFT JOIN pmph_user d on d.id = c.editor_id LEFT JOIN pmph_department e on e.id = d.department_id LEFT JOIN material_type f on f.id = b.type LEFT JOIN pmph_user g on g.id = b.editor_id where !a.is_deleted and b.id is not null
and b.bookname like CONCAT('%',#{bookname},'%')
and a.is_editor_replied = #{isEditorReplied}
ORDER BY a.gmt_create desc limit #{start} ,#{pageSize}
