mapper.select:
select a.id, b.bookname, a.page, a.line , a.content , a.attachment, a.attachment_name, d.realname, a.gmt_create , a.is_author_replied, a.author_reply , a.is_editor_handling , a.is_editor_replied , a.editor_reply, a.result from book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = a.book_id LEFT JOIN writer_user d on d.id = a.user_id where b.id is not null and c.id is not null and !a.is_deleted
and c.editor_id = #{editorId}
and b.bookname like CONCAT('%',#{bookname},'%')
and ((is_editor_replied and a.result = #{result} ) or (!is_editor_replied))
ORDER BY a.gmt_update desc limit #{start} ,#{pageSize}

SELECT count(a.id) FROM book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = b.id LEFT JOIN pmph_user d on d.id = c.editor_id LEFT JOIN pmph_department e on e.id = d.department_id LEFT JOIN material_type f on f.id = b.type where !a.is_deleted and b.id is not null
and b.bookname like CONCAT('%',#{bookname},'%')
and a.is_editor_replied = #{isEditorReplied}

SELECT a.id , b.bookname, e.dp_name , f.type_name , d.realname , a.result , a.page, a.line, a.content, a.author_reply, a.is_editor_replied FROM book_correction a LEFT JOIN book b on b.id = a.book_id LEFT JOIN book_editor c on c.book_id = b.id LEFT JOIN pmph_user d on d.id = c.editor_id LEFT JOIN pmph_department e on e.id = d.department_id LEFT JOIN material_type f on f.id = b.type where !a.is_deleted and b.id is not null
and b.bookname like CONCAT('%',#{bookname},'%')
and a.is_editor_replied = #{isEditorReplied}
ORDER BY a.gmt_update desc limit #{start} ,#{pageSize}
