update.foreach:
update cms_content
is_published = #{isPublished}, gmt_update = #{gmtUpdate},
where id=#{id};

mapper.select:
SELECT * FROM ( SELECT cc.`id`, cc.`title`, cc.author_name authorname, CASE WHEN cc.author_type = 2 THEN wu.username ELSE pu.`username` END username, cc.`auth_status`, cc.`auth_date`, cc.`gmt_create`, cc.`clicks`, cc.`comments`, cc.`likes`, cc.`bookmarks`, cc.category_id, ccg.`category_name`, cc.`is_published`, cc.`is_stick`, cc.`is_hot`, cc.`is_promote`, cc.`is_staging`, cc.`is_material_entry`, cc.author_type, cc.`author_id`, CASE WHEN cc.author_type = 2 THEN wu.realname ELSE pu.`realname` END realname, cc.apporpc, cc.`material_id` FROM `cms_content` cc LEFT JOIN `pmph_user` pu ON cc.author_id = pu.id LEFT JOIN `writer_user` wu ON cc.author_id = wu.id LEFT JOIN `cms_category` ccg ON cc.`category_id` = ccg.`id` WHERE cc.`is_deleted` = 0 AND ( cc.`author_type` IN (0, 1) OR ( cc.`author_type` = 2 AND cc.`is_staging` = FALSE ) ) ORDER BY cc.`gmt_create` DESC) a, ( SELECT COUNT(*) `COUNT` FROM `cms_content` cc LEFT JOIN `pmph_user` pu ON cc.author_id = pu.id LEFT JOIN `writer_user` wu ON cc.author_id = wu.id LEFT JOIN `cms_category` ccg ON cc.`category_id` = ccg.`id` WHERE cc.`is_deleted` = 0 AND ( cc.`author_type` IN (0, 1) OR ( cc.`author_type` = 2 AND cc.`is_staging` = FALSE ) )
AND cc.`category_id` = #{parameter.categoryId}
AND cc.`author_id`=#{parameter.authorId}
AND cc.`title` like concat('%',#{parameter.title},'%')
and
cc.`auth_status` = 0 and cc.author_type =2
cc.`auth_status` = 0 and cc.is_staging= 1
cc.`auth_status` = 1 and cc.author_type =2
cc.`auth_status` = 2 and cc.author_type =2 and cc.is_published=0
cc.`auth_status` = 2 and cc.is_published=1
cc.`auth_status` = 2 and cc.is_published=0
AND cc.`is_published` = #{parameter.isPublished}
AND ( pu.`username` like concat('%',#{parameter.username},'%') OR pu.`realname`like concat('%',#{parameter.username},'%') )
AND cc.`material_id` = #{parameter.materialId}
AND cc.`gmt_create` >= str_to_date(#{parameter.startCreateDate},'%Y-%m-%d %H:%i:%s')
AND cc.`gmt_create` <= str_to_date(#{parameter.endCreateDate},'%Y-%m-%d %H:%i:%s')
AND cc.`auth_date` >= str_to_date(#{parameter.startAuDate},'%Y-%m-%d %H:%i:%s')
AND cc.`auth_date` <= str_to_date(#{parameter.endAuDate},'%Y-%m-%d %H:%i:%s')
AND cc.`is_material_entry` = ${parameter.isMaterialEntry}
)b
`category_id` = #{parameter.categoryId}
AND `author_id`=#{parameter.authorId}
AND `title` like concat('%',#{parameter.title},'%')
and
`auth_status` = 0 and author_type =2
`auth_status` = 0 and is_staging= 1
`auth_status` = 1 and author_type =2
`auth_status` = 2 and author_type =2 and is_published=0
`auth_status` = 2 and is_published=1
`auth_status` = 2 and is_published=0
AND `is_published` = #{parameter.isPublished}
AND ( `username` like concat('%',#{parameter.username},'%') OR `realname`like concat('%',#{parameter.username},'%') )
AND `material_id` = #{parameter.materialId}
AND `gmt_create` >= str_to_date(#{parameter.startCreateDate},'%Y-%m-%d %H:%i:%s')
AND `gmt_create` <= str_to_date(#{parameter.endCreateDate},'%Y-%m-%d %H:%i:%s')
AND `auth_date` >= str_to_date(#{parameter.startAuDate},'%Y-%m-%d %H:%i:%s')
AND `auth_date` <= str_to_date(#{parameter.endAuDate},'%Y-%m-%d %H:%i:%s')
AND `is_material_entry` = ${parameter.isMaterialEntry}
order by `gmt_create` DESC
limit #{start},#{pageSize}

SELECT cc.`id`,cc.`title`,wu.realname username,cc.`auth_status`,cc.`auth_date`,cc.`gmt_create`,cc.`clicks`,pu.realname authUserName, cc.`comments`,cc.`likes`,cc.`bookmarks`,cc.category_id,ccg.`category_name`, cc.`is_published`,cc.`is_stick`,cc.`is_hot`,cc.`is_promote`,cc.`is_staging`,t.count FROM `cms_content`cc LEFT JOIN `writer_user` wu ON cc.author_id = wu.id LEFT JOIN pmph_user pu on pu.id = cc.auth_user_id LEFT JOIN `cms_category` ccg ON cc.`category_id` = ccg.`id`, ( SELECT count(*) count FROM `cms_content`cc LEFT JOIN `writer_user` wu ON cc.author_id = wu.id LEFT JOIN pmph_user pu on pu.id = cc.auth_user_id LEFT JOIN `cms_category` ccg ON cc.`category_id` = ccg.`id` WHERE cc.`is_deleted`= 0 AND cc.`is_staging` = FALSE
AND cc.`category_id` = #{parameter.categoryId}
AND cc.`author_id`=#{parameter.authorId}
AND cc.`title` like concat('%',#{parameter.title},'%')
AND cc.`auth_status` = #{parameter.authStatus}
AND cc.`is_published` = #{parameter.isPublished}
AND ( wu.`username` like concat('%',#{parameter.username},'%') OR wu.`realname` like concat('%',#{parameter.username},'%') )
) t WHERE cc.`is_deleted`= 0 AND cc.`is_staging` = FALSE
AND cc.`category_id` = #{parameter.categoryId}
AND cc.`author_id`=#{parameter.authorId}
AND cc.`title` like concat('%',#{parameter.title},'%')
AND cc.`auth_status` = #{parameter.authStatus}
AND cc.`is_published` = #{parameter.isPublished}
AND ( wu.`username` like concat('%',#{parameter.username},'%') OR wu.`realname`like concat('%',#{parameter.username},'%') )
order by cc.`gmt_create` DESC
limit #{start},#{pageSize}

mapper.update:
update
	cms_content set is_deleted =true
where material_id = #{materialId};

mapper.select:
SELECT cc.`id`,cc.`title`,pu.`username`,pu.`realname`,cc.`gmt_create`,t.count FROM `cms_content`cc LEFT JOIN `pmph_user` pu ON cc.author_id = pu.id, ( SELECT count(*) count FROM `cms_content`cc LEFT JOIN `pmph_user` pu ON cc.author_id = pu .id WHERE cc.`is_deleted`= 0 AND cc.`is_staging` = FALSE
AND cc.`category_id` = #{parameter.categoryId}
AND cc.`title` like concat('%',#{parameter.title},'%')
AND ( pu.`username` like concat('%',#{parameter.username},'%') OR pu.`realname` like concat('%',#{parameter.username},'%') )
) t WHERE cc.`is_deleted`= 0 AND cc.`is_staging` = FALSE
AND cc.`category_id` = #{parameter.categoryId}
AND cc.`title` like concat('%',#{parameter.title},'%')
AND ( pu.`username` like concat('%',#{parameter.username},'%') OR pu.`realname` like concat('%',#{parameter.username},'%') )
order by cc.`gmt_create` DESC
limit #{start},#{pageSize}

SELECT count(*) FROM ( SELECT ! ISNULL(bb1.id) AS relationCms, b.title as cmsTitle, b.author_name as cmsAuthorName, b.id as id, b.gmt_create FROM cms_content b LEFT JOIN cms_relationship bb1 ON b.id = bb1.related_cms_id AND bb1.main_cms_id = #{parameter.currentCmsId}
WHERE 1 = 1 and b.`is_deleted`= 0 AND b.`is_staging` = FALSE AND b.auth_status = 2 -- 必须是已通过的文章 and b.category_id = 1 AND b.id != #{parameter.currentCmsId} and b.apporpc != #{parameter.apporpc}
)t WHERE 1 = 1
and relationCms = #{parameter.relationCms}
and ( t.cmsAuthorName like concat('%',#{parameter.cmsAuthorName},'%'))
and ( t.cmsTitle like concat('%',#{parameter.cmsTitle},'%'))
ORDER BY t.relationCms DESC,t.gmt_create DESC

SELECT * FROM ( SELECT ! ISNULL(bb1.id) AS relationCms, b.title as cmsTitle, b.author_name as cmsAuthorName, b.id as id, b.gmt_create FROM cms_content b LEFT JOIN cms_relationship bb1 ON b.id = bb1.related_cms_id AND bb1.main_cms_id = #{parameter.currentCmsId}
WHERE 1 = 1 and b.`is_deleted`= 0 AND b.`is_staging` = FALSE and b.category_id = 1 and b.auth_status = 2 -- 必须是已通过的文章 AND b.id != #{parameter.currentCmsId} and b.apporpc != #{parameter.apporpc} )t WHERE 1 = 1
and relationCms = #{parameter.relationCms}
and ( t.cmsAuthorName like concat('%',#{parameter.cmsAuthorName},'%'))
and ( t.cmsTitle like concat('%',#{parameter.cmsTitle},'%'))
ORDER BY t.relationCms DESC,t.gmt_create DESC LIMIT #{start},#{pageSize}

mapper.delete:
DELETE
FROM
	cms_relationship
WHERE
	main_cms_id = #{currentCmsId}
  AND related_cms_id = #{relationCmsId}
