and e.org_name like concat ('%',#{parameter.schoolName},'%')
GROUP BY a.org_id ORDER BY SUM(if((b.chosen_position in (4,12)),1,0)) DESC, SUM(if((b.chosen_position in (2,10)),1,0)) DESC, SUM(if((b.chosen_position in (1,9)),1,0)) DESC, SUM(if((b.chosen_position in (8,9,10,12)),1,0)) DESC)t1, (SELECT @rowno:=0)t2 )t3 ;

SELECT * FROM( SELECT @rowno:=@rowno+1 AS row,t1.materialid,t1.orgid,t1.schoolname,t1.editorlist, t1.subeditorlist,t1.editoriallist,t1.isdigitaleditorlist FROM ( SELECT GROUP_CONCAT(if((b.chosen_position =1),a.realname,null)) editorlist, GROUP_CONCAT(if((b.chosen_position =2),a.realname,null)) subeditorlist, GROUP_CONCAT(if((b.chosen_position >=3),a.realname,null)) editoriallist, GROUP_CONCAT(if((b.chosen_position IN (8,9,10,12)),a.realname,null)) isdigitaleditorlist, d.id materialid,e.id orgid,e.org_name schoolname FROM declaration a LEFT JOIN dec_position_published b ON a.id = b.declaration_id LEFT JOIN textbook c ON b.textbook_id = c.id LEFT JOIN material d ON c.material_id = d.id LEFT JOIN (SELECT id,org_name FROM org UNION SELECT 0,'人民卫生出版社' FROM org)e ON a.org_id = e.id WHERE a.is_staging = 0 AND a.online_progress > 0 AND a.org_id IS NOT NULL AND d.id = #{parameter.materialId}
and e.org_name like concat ('%',#{parameter.schoolName},'%')
GROUP BY a.org_id ORDER BY SUM(if((b.chosen_position =1),1,0)) DESC, SUM(if((b.chosen_position =2),1,0)) DESC, SUM(if((b.chosen_position >=3),1,0)) DESC, SUM(if((b.chosen_position in (8,9,10,12)),1,0)) DESC)t1, (SELECT @rowno:=0)t2 )t3 ;

SELECT * FROM( SELECT @rowno:=@rowno+1 AS row,t1.materialid,t1.orgid,t1.schoolname, t1.bookname,t1.editorlist, t1.subeditorlist,t1.editoriallist,t1.isdigitaleditorlist FROM ( SELECT GROUP_CONCAT(if((b.chosen_position IN (4,12)),a.realname,null)) editorlist, GROUP_CONCAT(if((b.chosen_position IN (2,10)),a.realname,null)) subeditorlist, GROUP_CONCAT(if((b.chosen_position IN (1,9)),a.realname,null)) editoriallist, GROUP_CONCAT(if((b.chosen_position IN (8,9,10,12)),a.realname,null)) isdigitaleditorlist, d.id materialid,e.id orgid,e.org_name schoolname, c.textbook_name bookname FROM declaration a LEFT JOIN dec_position_published b ON a.id = b.declaration_id LEFT JOIN textbook c ON b.textbook_id = c.id LEFT JOIN material d ON c.material_id = d.id LEFT JOIN (SELECT id,org_name FROM org UNION SELECT 0,'人民卫生出版社' FROM org)e ON a.org_id = e.id WHERE a.is_staging = 0 AND a.online_progress > 0 AND a.org_id IS NOT NULL AND d.id = #{parameter.materialId}
and e.org_name like concat ('%',#{parameter.schoolName},'%')
GROUP BY a.org_id, c.textbook_name ORDER BY SUM(if((b.chosen_position in (4,12)),1,0)) DESC, SUM(if((b.chosen_position in (2,10)),1,0)) DESC, SUM(if((b.chosen_position in (1,9)),1,0)) DESC, SUM(if((b.chosen_position in (8,9,10,12)),1,0)) DESC)t1, (SELECT @rowno:=0)t2 )t3 ORDER BY t3.schoolname, t3.bookname ASC
limit #{start},#{pageSize}
;

SELECT * FROM( SELECT @rowno:=@rowno+1 AS row,t1.materialid,t1.orgid,t1.schoolname, t1.bookname,t1.editorlist, t1.subeditorlist,t1.editoriallist,t1.isdigitaleditorlist FROM ( SELECT GROUP_CONCAT(if((b.chosen_position =1),a.realname,null)) editorlist, GROUP_CONCAT(if((b.chosen_position =2),a.realname,null)) subeditorlist, GROUP_CONCAT(if((b.chosen_position =3),a.realname,null)) editoriallist, GROUP_CONCAT(if((b.chosen_position IN (8,9,10,12)),a.realname,null)) isdigitaleditorlist, d.id materialid,e.id orgid,e.org_name schoolname, c.textbook_name bookname FROM declaration a LEFT JOIN dec_position_published b ON a.id = b.declaration_id LEFT JOIN textbook c ON b.textbook_id = c.id LEFT JOIN material d ON c.material_id = d.id LEFT JOIN (SELECT id,org_name FROM org UNION SELECT 0,'人民卫生出版社' FROM org)e ON a.org_id = e.id WHERE a.is_staging = 0 AND a.online_progress > 0 AND a.org_id IS NOT NULL AND d.id = #{parameter.materialId}
and e.org_name like concat ('%',#{parameter.schoolName},'%')
GROUP BY a.org_id, c.textbook_name ORDER BY SUM(if((b.chosen_position =1),1,0)) DESC, SUM(if((b.chosen_position =2),1,0)) DESC, SUM(if((b.chosen_position =3),1,0)) DESC, SUM(if((b.chosen_position in (8,9,10,12)),1,0)) DESC)t1, (SELECT @rowno:=0)t2 )t3 ORDER BY t3.schoolname, t3.bookname ASC
limit #{start},#{pageSize}
;

SELECT * FROM ( SELECT @rowno:=@rowno+1 AS row,t1.materialid,t1.id,t1.bookname,t1.editorlist, t1.subeditorlist,t1.editoriallist,t1.isdigitaleditorlist FROM ( SELECT '-' editorlist,'-' subeditorlist,'-' editoriallist,'-' isdigitaleditorlist, b.id materialid,a.id id,a.textbook_name bookname FROM textbook a LEFT JOIN material b ON a.material_id = b.id WHERE b.id = #{parameter.materialId}
and a.textbook_name like concat ('%',#{parameter.bookName},'%')
GROUP BY a.id ORDER BY a.sort ASC)t1, (SELECT @rowno:=0)t2 )t3
limit #{start},#{pageSize}
;

