and c.textbook_name like concat ('%',#{parameter.bookName},'%')
GROUP BY c.id ORDER BY c.sort ASC)t1, (SELECT @rowno:=0)t2 )t3 ;

SELECT * FROM ( SELECT @rowno:=@rowno+1 AS row,t1.materialid,t1.id,t1.bookname,t1.editorlist, t1.subeditorlist,t1.editoriallist,t1.isdigitaleditorlist FROM ( SELECT GROUP_CONCAT(if((b.chosen_position =1),a.realname,null)) editorlist, GROUP_CONCAT(if((b.chosen_position =2),a.realname,null)) subeditorlist, GROUP_CONCAT(if((b.chosen_position >=3),a.realname,null)) editoriallist, GROUP_CONCAT(IF((b.chosen_position IN (8,9,10,12)),a.realname,null)) isdigitaleditorlist, d.id materialid,c.id id,c.textbook_name bookname FROM declaration a LEFT JOIN dec_position_published b ON a.id = b.declaration_id LEFT JOIN textbook c ON b.textbook_id = c.id LEFT JOIN material d ON c.material_id = d.id WHERE a.is_staging = 0 AND a.online_progress > 0 AND d.id = #{parameter.materialId}
and c.textbook_name like concat ('%',#{parameter.bookName},'%')
GROUP BY c.id ORDER BY c.sort ASC)t1, (SELECT @rowno:=0)t2 )t3 ;

select * from dec_position dp
where dp.chosen_position & 1 >0
and dp.textbook_id=#{textbookId}

