d.`org_name`, t.`count` FROM declaration d LEFT JOIN (SELECT * FROM `textbook` GROUP BY `textbook_name`)tb ON d.`material_id`= tb.`material_id`
JOIN `org` o ON o.`id`=d.`org_id`, ( SELECT COUNT(*) `count` FROM declaration d LEFT JOIN (SELECT * FROM `textbook` GROUP BY `textbook_name`)tb ON d.`material_id`= tb.`material_id`
JOIN `org` o ON o.`id`=d.`org_id`
d.`is_staging`=FALSE
AND tb.`id` IN
#{item}
)t
d.`is_staging`=FALSE
AND tb.`id` IN
#{item}
GROUP BY d.`user_id` ORDER BY d.`realname`
limit #{start},#{pageSize}

SELECT * FROM ( SELECT @rowno:=@rowno+1 AS row,t1.materialid,t1.orgid,t1.schoolname, t1.presetpositioneditor,t1.presetpositionsubeditor,t1.presetpositioneditorial, t1.presetdigitaleditor FROM ( SELECT SUM(if((b.preset_position IN (4,5,6,7,12,13,14,15)),1,0)) presetpositioneditor, SUM(if((b.preset_position IN (2,3,6,7,10,11,14,15)),1,0)) presetpositionsubeditor, SUM(if((b.preset_position IN (1,3,5,7,9,11,13,15)),1,0)) presetpositioneditorial, SUM(if((b.preset_position IN (8,9,10,11,12,13,14,15)),1,0)) presetdigitaleditor, d.id materialid,e.id orgid,e.org_name schoolname FROM declaration a LEFT JOIN dec_position b ON a.id = b.declaration_id LEFT JOIN textbook c ON b.textbook_id = c.id LEFT JOIN material d ON c.material_id = d.id LEFT JOIN (SELECT id,org_name FROM org UNION SELECT 0,'人民卫生出版社' FROM org)e ON a.org_id = e.id WHERE a.is_staging = 0 AND a.online_progress > 0 AND b.preset_position IS NOT NULL AND a.org_id IS NOT NULL AND d.id = #{parameter.materialId}
and e.org_name like concat ('%',#{parameter.schoolName},'%')
GROUP BY e.id ORDER BY SUM(if((b.preset_position IN (4,5,6,7,12,13,14,15)),1,0))DESC, SUM(if((b.preset_position IN (2,3,6,7,10,11,14,15)),1,0)) DESC, SUM(if((b.preset_position IN (1,3,5,7,9,11,13,15)),1,0)) DESC, SUM(if((b.preset_position IN (8,9,10,11,12,13,14,15)),1,0)) DESC)t1, (SELECT @rowno:=0)t2 )t3
limit #{start},#{pageSize}
