mapper.select:
select * from declaration where true

	and id in
	
		#{id}

select * from declaration where
material_id=#{materialId}
and user_id=#{userId};

SELECT t.textbook_name, t.textbook_round, d.*,w.username, GROUP_CONCAT( CASE when p.preset_position =15 then '主编,副主编,编委,数字编委' when p.preset_position =14 then '主编,副主编,数字编委' when p.preset_position =13 then '主编,编委,数字编委' when p.preset_position =12 then '主编,数字编委' when p.preset_position =11 then '副主编,编委,数字编委' when p.preset_position =10 then '副主编,数字编委' when p.preset_position =9 then '编委,数字编委' when p.preset_position =8 then '数字编委' when p.preset_position =7 then '主编,副主编,编委' when p.preset_position =6 then '主编,副主编' when p.preset_position =5 then '主编,编委' when p.preset_position =4 then '主编' when p.preset_position =3 then '副主编,编委' when p.preset_position =2 then '副主编' when p.preset_position =1 then '编委' else null END ORDER BY t.textbook_name ) presetPosition, if(d.org_id=0,'人民卫生出版社',o.org_name) orgNameOne FROM declaration d LEFT JOIN writer_user w ON w.id = d.user_id LEFT JOIN dec_position p ON p.declaration_id = d.id LEFT JOIN textbook t ON t.id = p.textbook_id LEFT JOIN org o ON o.id = d.org_id LEFT JOIN material m ON m.id = d.material_id WHERE d.online_progress > 0 AND d.is_deleted = 0 AND m.is_deleted = 0 AND d.id IN
#{id}
GROUP BY t.textbook_round ,t.textbook_name, w.username ORDER BY t.textbook_name DESC

SELECT t.textbook_name, t.textbook_round, d.*,w.username, GROUP_CONCAT( p.preset_position ORDER BY t.textbook_name ) presetPosition, if(d.org_id=0,'人民卫生出版社',o.org_name) orgNameOne FROM declaration d LEFT JOIN writer_user w ON w.id = d.user_id LEFT JOIN dec_position p ON p.declaration_id = d.id LEFT JOIN textbook t ON t.id = p.textbook_id LEFT JOIN org o ON o.id = d.org_id LEFT JOIN material m ON m.id = d.material_id WHERE d.online_progress > 0 AND d.is_deleted = 0 AND m.is_deleted = 0 AND d.id IN
#{id}
GROUP BY t.textbook_round ,t.textbook_name, w.username ORDER BY t.textbook_name DESC
