mapper.select:
select * from declaration where true

	and id in
	
		#{id}

select * from declaration where
material_id=#{materialId}
and user_id=#{userId};

SELECT t.textbook_name, t.textbook_round, d.*,w.username, GROUP_CONCAT( CASE when p.preset_position =15 then '主编,副主编,编委,数字编委' when p.preset_position =14 then '主编,副主编,数字编委' when p.preset_position =13 then '主编,编委,数字编委' when p.preset_position =12 then '主编,数字编委' when p.preset_position =11 then '副主编,编委,数字编委' when p.preset_position =10 then '副主编,数字编委' when p.preset_position =9 then '编委,数字编委' when p.preset_position =8 then '数字编委' when p.preset_position =7 then '主编,副主编,编委' when p.preset_position =6 then '主编,副主编' when p.preset_position =5 then '主编,编委' when p.preset_position =4 then '主编' when p.preset_position =3 then '副主编,编委' when p.preset_position =2 then '副主编' when p.preset_position =1 then '编委' else null END ORDER BY t.textbook_name ) presetPosition, if(d.org_id=0,'人民卫生出版社',o.org_name) orgNameOne FROM declaration d LEFT JOIN writer_user w ON w.id = d.user_id LEFT JOIN dec_position p ON p.declaration_id = d.id LEFT JOIN textbook t ON t.id = p.textbook_id LEFT JOIN org o ON o.id = d.org_id LEFT JOIN material m ON m.id = d.material_id WHERE d.online_progress > 0 AND d.is_deleted = 0 AND m.is_deleted = 0 AND d.id IN
#{id}
GROUP BY t.textbook_round ,t.textbook_name, w.username ORDER BY t.textbook_name DESC

select a.id, a.material_id, a.realname, e.username, f.org_name unitName, a.org_name, a.position, a.title, a.handphone, a.email, GROUP_CONCAT( CONCAT(d.textbook_name,'-', case when c.preset_position =15 then '主编,副主编,编委,数字编委' when c.preset_position =14 then '主编,副主编,数字编委' when c.preset_position =13 then '主编,编委,数字编委' when c.preset_position =12 then '主编,数字编委' when c.preset_position =11 then '副主编,编委,数字编委' when c.preset_position =10 then '副主编,数字编委' when c.preset_position =9 then '编委,数字编委' when c.preset_position =8 then '数字编委' when c.preset_position =7 then '主编,副主编,编委' when c.preset_position =6 then '主编,副主编' when c.preset_position =5 then '主编,编委' when c.preset_position =4 then '主编' when c.preset_position =3 then '副主编,编委' when c.preset_position =2 then '副主编' when c.preset_position =1 then '编委' else null end ) ORDER BY d.sort Separator '</br>' )chooseBooksAndPostions,a.online_progress,a.offline_progress from declaration a
LEFT JOIN dec_position c on c.declaration_id = a.id LEFT JOIN textbook d on d.id = c.textbook_id LEFT JOIN writer_user e on e.id=a.user_id LEFT JOIN (SELECT id,org_name from org UNION SELECT 0,'人民卫生出版社' from org ) f on f.id=a.org_id where a.online_progress > 0 and a.is_deleted = 0 and c.id is not null
and a.material_id in ${parameter.materialId}
and d.id in
#{item}
and (a.realname like concat('%',#{parameter.realname},'%') or e.username like concat('%',#{parameter.realname},'%') )
and a.position like concat('%',#{parameter.position},'%')
and a.title like concat('%',#{parameter.title},'%')
and a.org_name like concat('%',#{parameter.orgName},'%')
and a.org_id = #{parameter.orgId}
and f.org_name like concat('%',#{parameter.unitName},'%')
and c.preset_position in (4,5,6,7,12,13,14,15)
and c.preset_position in (2,3,6,7,10,11,14,15)
and c.preset_position in (1,3,5,7,9,11,13,15)
and a.online_progress = #{parameter.onlineProgress}
and a.offline_progress = #{parameter.offlineProgress}
GROUP BY a.id
HAVING GROUP_CONCAT(c.syllabus_id SEPARATOR "") != ""
HAVING GROUP_CONCAT(c.syllabus_id SEPARATOR "") = ""
order by a.realname limit #{start},#{pageSize}
