mapper.select:
SELECT sq.`title`, sqa.`question_id`, sqa.`survey_id`, GROUP_CONCAT(sqa.`option_id`) option_id, GROUP_CONCAT(sqo.`option_content`) oneOrManyOptionContent, sqa.`option_content` noteOptionContent FROM `material_survey_question_answer` sqa JOIN `material_survey_question` sq ON sqa.`question_id` = sq.`id` LEFT JOIN `survey_question_option` sqo ON sqa.`option_id`= sqo.`id`
sqa.`survey_id` = #{surveyId}
AND sqa.`user_id`= #{userId}
GROUP BY sq.`title` ORDER BY sq.`id` ASC

SELECT s.`id` surveyId, s.`title` surveyTitle, s.`begin_date` startDate, s.`end_date`, st.`survey_name`, s.`intro`, COUNT(DISTINCT sqa.`user_id`) surveyUsers, pu.`realname`, t.count FROM `material_survey_target` sta JOIN `material_survey_question_answer` sqa ON sta.`survey_id` = sqa.`survey_id` JOIN `material_survey` s ON sqa.`survey_id` = s.`id` and s.is_deleted = 0 JOIN `survey_type` st ON s.`type_id` = st.`id` JOIN `pmph_user` pu ON pu.`id`= sta.`user_id`, ( SELECT COUNT(DISTINCT sqa.`user_id`) `count` FROM `material_survey_target` sta JOIN `material_survey_question_answer` sqa ON sta.`survey_id` = sqa.`survey_id` JOIN `material_survey` s ON sqa.`survey_id` = s.`id` JOIN `survey_type` st ON s.`type_id` = st.`id` JOIN `pmph_user` pu ON pu.`id` = sta.`user_id`
s.`title` like concat('%',#{parameter.surveyTitle},'%')
)t
s.`title` like concat('%',#{parameter.surveyTitle},'%')
GROUP BY sqa.`survey_id`
limit #{start},#{pageSize}

SELECT c.id questionId, c.title, c.type questionType, GROUP_CONCAT(c.option_id)optionId, GROUP_CONCAT(c.option_content)optionContent, GROUP_CONCAT(c.counts) optionCount, SUM(c.counts) questionSum FROM (SELECT a.id, a.title, a.type, a.option_id, a.option_content, COUNT(b.optId) counts FROM( SELECT sq.`id`, sq.`title`, sq.`type`, sqo.`id` option_id, sqo.`option_content` FROM `material_survey_question` sq JOIN `survey_question_option` sqo ON sq.`id` = sqo.`question_id` JOIN `survey_template_question` stq ON sq.`id` = stq.`question_id` JOIN `material_survey` s ON stq.`template_id` = s.`template_id`
s.`id`=#{surveyId}
ORDER BY sq.id ASC ) a LEFT JOIN ( SELECT sqa.`question_id`, sqa.`option_id` optId, sqa.`option_content` optCont FROM `material_survey_question_answer` sqa ) b ON a.id = b.question_id AND a.option_id = b.optId GROUP BY a.option_content ORDER BY a.id ASC, a.option_id ASC ) c GROUP BY c.title

SELECT sq.`title` questionName, sqa.`option_content`, DATE_FORMAT(sqa.`gmt_create`, '%Y-%c-%d %h:%i:%s') gmt_create, wu.`realname`, t.count FROM `material_survey_question_answer` sqa JOIN `material_survey_question` sq ON sqa.`question_id` = sq.`id` JOIN `writer_user` wu ON sqa.`user_id` = wu.`id`, ( SELECT count(*) count FROM `material_survey_question_answer` sqa JOIN `material_survey_question` sq ON sqa.`question_id` = sq.`id` JOIN `writer_user` wu ON sqa.`user_id` = wu.`id`
sq.`type` IN (4, 5)
AND sqa.`survey_id`=#{parameter.surveyId}
AND sqa.`question_id`=#{parameter.questionId}
)t
sq.`type` IN (4, 5)
AND sqa.`survey_id`=#{parameter.surveyId}
AND sqa.`question_id`=#{parameter.questionId}
ORDER BY sq.`sort`
limit #{start},#{pageSize}
