mapper.select:
SELECT stq.`id`,stq.`title`, stq.`type`, stq.`sort`, GROUP_CONCAT(sqo.`id`) optionIdString, GROUP_CONCAT(sqo.`option_content`) optionContent FROM `material_survey_template` st LEFT JOIN `material_survey_template_question` stq ON st.`id` = stq.`survey_id` /*LEFT JOIN `survey_question` sq ON sq.`id`= stq.`question_id`*/ LEFT JOIN `material_survey_template_question_option`sqo ON sqo.`question_id`= stq.`id` WHERE st.`is_deleted` =FALSE
AND st.`id`=#{templateId}
GROUP BY stq.`question_id`

select s.title,s.subhead,s.intro from material_survey_template st
left join survey s on s.template_id = st.id 
where id = #{id};
