mapper.select:
SELECT ou.`id`,o.`org_name`,ou.`username`,ou.`realname`,ou.`handphone`, ou.`email`,ou.`position`,ou.`title`,ou.`address`, ou.`postcode`,ou.`proxy`,ou.`progress`,t.count FROM `org` o LEFT JOIN `org_user` ou ON o.`id`=ou.`org_id`, ( select count(*) count from org o left join `org_user` ou ON o.`id`=ou.`org_id` where ou.is_deleted = 0
and o.org_name like concat(concat('%',#{parameter.orgName}),'%')
and ou.username like concat(concat('%',#{parameter.username}),'%')
and ou.`realname` like concat(concat('%',#{parameter.realname}),'%')
and ou.progress = #{parameter.progress}
)t where ou.is_deleted = 0
and o.org_name like concat(concat('%',#{parameter.orgName}),'%')
and ou.username like concat(concat('%',#{parameter.username}),'%')
and ou.`realname` like concat(concat('%',#{parameter.realname}),'%')
and ou.progress = #{parameter.progress}
order by ou.sort
limit #{start},#{pageSize}

select a.id,a.org_name,c.type_name orgTypeName,a.org_type_id,b.id areaId,a.parent_id,a.gmt_create ,b.area_name,a.contact_person,a.contact_phone,a.sort,a.note,t.count from org a left join area b on a.area_id =b.id left join org_type c on a.org_type_id=c.id, ( select count(*) count from org a left join area b on a.area_id =b.id left join org_type c on a.org_type_id=c.id where a.is_deleted = 0
and a.org_name like concat(concat('%',#{parameter.orgName}),'%')
and c.id = #{parameter.orgTypeId}
and b.id = #{parameter.areaId}
)t where a.is_deleted = 0
and a.org_name like concat(concat('%',#{parameter.orgName}),'%')
and c.id = #{parameter.orgTypeId}
and b.id = #{parameter.areaId}
order by a.sort
limit #{start},#{pageSize}

SELECT GROUP_CONCAT(o.`id`) id, GROUP_CONCAT(o.`org_name`)org_name, o.`area_id`, o.`parent_id`, GROUP_CONCAT(o.`org_type_id`)org_type_id, ar.`area_name` FROM `org` o LEFT JOIN `org_type` ot ON o.`org_type_id` = ot.`id` LEFT JOIN `area` ar ON o.`area_id` = ar.`id` WHERE 1=1
and o.`org_name` LIKE CONCAT(CONCAT('%',#{orgName},'%'))
GROUP BY o.`area_id` ORDER BY o.`area_id`,o.`id` ASC;
