mapper.select:
select g.id, g.group_name, g.group_image, g.gmt_last_message gmtLastMessage, g.book_id, t.textbook_name bookName, (select CONCAT(if(pge.is_writer=1,wu.realname,pu.realname),':',pgm.msg_content)msg_content from pmph_group_message pgm LEFT JOIN pmph_group_member pge on pgm.member_id=pge.id left join writer_user wu on wu.id=pge.user_id left join pmph_user pu on pu.id=pge.user_id where pgm.group_id=g.id and pgm.member_id >0 ORDER BY pgm.gmt_create desc LIMIT 0,1) as msg_content from pmph_group g LEFT JOIN textbook t ON g.book_id = t.id where g.id in ( select DISTINCT group_id from pmph_group_member where user_id = #{id} and is_writer = false AND is_deleted = FALSE )
and g.group_name like concat(concat('%',#{pmphGroup.groupName}),'%')
GROUP BY g.id order by gmt_last_message desc , group_name ;

SELECT g.id, g.group_name, g.group_image, g.gmt_last_message gmtLastMessage, g.book_id, t.textbook_name bookName, (select CONCAT(if(pge.is_writer=1,wu.realname,pu.realname),':',pgm.msg_content) msg_content from pmph_group_message pgm LEFT JOIN pmph_group_member pge on pgm.member_id=pge.id left join writer_user wu on wu.id=pge.user_id left join pmph_user pu on pu.id=pge.user_id where pgm.group_id=g.id and pgm.member_id >0 ORDER BY pgm.gmt_create desc LIMIT 0,1) as msg_content, COUNT(f.id) files FROM pmph_group g LEFT JOIN textbook t ON g.book_id = t.id LEFT JOIN pmph_group_file f ON f.group_id = g.id where 1=1
and g.group_name like concat('%',#{groupName},'%')
GROUP BY g.id order by gmt_last_message desc , group_Name ;

select g.id,g.group_name,g.group_image,g.gmt_last_message gmtLastMessage, g.book_id,t.textbook_name bookName from pmph_group g LEFT JOIN textbook t ON g.book_id = t.id where g.id in ( select DISTINCT group_id from pmph_group_member where 1=1
and user_id = #{parameter.id}
and is_writer =false AND is_deleted = FALSE)
and g.group_name like concat(concat('%',#{parameter.groupName}),'%')
GROUP BY g.id order by gmt_last_message desc , group_name
limit #{start},#{pageSize}

select count(*) from pmph_group g LEFT JOIN textbook t ON g.book_id = t.id where g.id in (select DISTINCT group_id from pmph_group_member where 1=1
and user_id = #{parameter.id}
and is_writer =false AND is_deleted = FALSE)
