mapper.update:
update pmph_user set password =
#{newPassword} where
id = #{id} and password = #{oldPassword}

mapper.select:
SELECT * FROM pmph_user
WHERE username LIKE CONCAT(CONCAT('%',#{name},'%')) AND realname LIKE CONCAT(CONCAT('%',#{name},'%'))
LIMIT #{start},#{size}

		SELECT * FROM pmph_user WHERE
		1=1
		 and username = #{username}
		 and id = #{id}

SELECT id, username, is_disabled, realname, department_id, note, avatar, is_deleted, is_director FROM pmph_user WHERE 1=1
and username = #{username}
and password = #{password}

SELECT count(*) FROM pmph_user a LEFT JOIN pmph_department b ON a.department_id = b.id WHERE (b.path LIKE concat(#{path},'%') OR b.id =#{departmentId} OR b.parent_id =#{departmentId})
AND a.realname like concat('%',#{realName},'%')
;

SELECT a.id,a.department_id,a.realname,a.handphone,b.dp_name departmentName,a.openid FROM pmph_user a LEFT JOIN pmph_department b ON a.department_id = b.id WHERE (b.path LIKE concat(#{path},'%') OR b.id =#{departmentId} OR b.parent_id =#{departmentId})
AND a.realname like concat('%',#{realName},'%')
LIMIT #{start},#{pageSize};

select u.id ,u.realname realname ,u.department_id departmentId ,u.is_director isDirector ,u.openid from pmph_user u inner join (select concat(d.path,'-',d.id) as fullPath from pmph_user u join pmph_department d on u.department_id = d.id where u.id = #{userId} /*某人user_id*/) preg join pmph_user_role ur on ur.user_id = u.id join pmph_role r on ur.role_id = r.id
and r.id = #{role_id}
and r.role_name = #{role_name}
where preg.fullPath Regexp concat('-',u.department_id,'(-[0-9]+)*$') group by u.id
