mapper.select:
select tb.*,ifnull(c.surveyNum,0) as surveyNum from textbook tb left join ( select textbook_id,count(id) as surveyNum from material_survey_chain
where material_survey_id = #{materialSurveyId}
group by textbook_id ) c on c.textbook_id = tb.id
where tb.`material_id`=#{materialId} order by tb.sort asc;

select count(*) from ( select a.id from textbook a LEFT JOIN dec_position b on b.textbook_id=a.id LEFT JOIN (select * from declaration where is_deleted = 0 and is_staging = 0 and online_progress !=0 ) c on c.id = b.declaration_id LEFT JOIN material d on d.id = a.material_id LEFT JOIN (select * from pmph_user where is_deleted =0 and is_disabled =0) planningEditor on planningEditor.id = a.planning_editor LEFT JOIN pmph_group e on e.book_id = a.id where d.is_deleted =0 and d.is_published =1 and d.id = #{parameter.materialId}
and a.planning_editor = #{parameter.pmphUserId}
and a.is_locked = 0
and a.is_locked = 1
and a.is_published =1
and d.is_force_end =1
and a.id in
#{item}
and a.textbook_name like concat ('%',#{parameter.bookName},'%')
GROUP BY a.id )temp

select e.book_id bookId, a.revision_times, a.republish_times, a.is_locked, a.is_published, d.id materialId, d.is_all_textbook_published isAllTextbookPublished, d.is_force_end isForceEnd, a.id textBookId, a.sort sort, a.textbook_name, a.textbook_round textbookRound, a.is_chief_published, sum(IF(b.id is not null ,1,0)) applyNum, a.planning_editor planningEditor, planningEditor.realname planningEditorName , GROUP_CONCAT( IF( b.chosen_position in (2,3,4,5,6,7,10,11,12,13,14,15), IF(g.allres like CONCAT('%,',b.textbook_id,'_',b.declaration_id,'_',b.preset_position,'_',b.chosen_position,'_',IFNULL(b.rank,0),',%') or a.is_published or a.is_chief_published, CONCAT('<font color="#2828ca" style="font-weight: 700;">',c.realname,'</font>'), CONCAT('<font color="black">',c.realname,'</font>')), null ) ORDER BY if(b.chosen_position>8,b.chosen_position-8,b.chosen_position) desc,b.rank asc) editorsAndAssociateEditors, sum(IF(b.chosen_position in (2,3,4,5,6,7,10,11,12,13,14,15),1,0)) editorsAndAssociateEditorsNum, GROUP_CONCAT( IF( b.chosen_position in (1,9), IF(g.allres like CONCAT('%,',b.textbook_id,'_',b.declaration_id,'_',b.preset_position,'_',b.chosen_position,'_',IFNULL(b.rank,0),',%'), CONCAT('<font color="black">',c.realname,'</font>'),CONCAT('<font color="black">',c.realname,'</font>')), null ) ORDER BY b.chosen_position desc,b.rank asc) bianWeis, sum(IF(b.chosen_position in (1,3,5,7) ,1,0)) bianWeisNum , e.id groupId, IF(!a.is_published,null,f.repub) repub from textbook a LEFT JOIN dec_position b on b.textbook_id=a.id LEFT JOIN (select * from declaration where is_deleted = 0 and is_staging = 0 and online_progress !=0) c on c.id = b.declaration_id LEFT JOIN material d on d.id = a.material_id LEFT JOIN (select * from pmph_user where is_deleted =0 and is_disabled =0) planningEditor on planningEditor.id = a.planning_editor LEFT JOIN pmph_group e on e.book_id = a.id
LEFT JOIN ( SELECT textbook_id,IF(GROUP_CONCAT(PUB ORDER BY PUB ASC ) != GROUP_CONCAT(RES ORDER BY PUB ASC ),true,false) repub FROM ( select A.textbook_id,IFNULL(a.PUB,'')PUB ,IFNULL(b.RES,'')RES from ( SELECT textbook_id,declaration_id, CONCAT(textbook_id,'_',declaration_id,'_',preset_position,'_',chosen_position,'_',IFNULL(rank,0)) PUB from dec_position_published ) a left JOIN ( SELECT textbook_id,declaration_id, CONCAT(textbook_id,'_',declaration_id,'_',preset_position,'_',chosen_position,'_',IFNULL(rank,0)) RES from dec_position WHERE chosen_position !=0 ) b on b.textbook_id = a.textbook_id and a.declaration_id = b.declaration_id UNION select B.textbook_id,IFNULL(a.PUB,'')PUB ,IFNULL(b.RES,'')RES from ( SELECT textbook_id,declaration_id, CONCAT(textbook_id,'_',declaration_id,'_',preset_position,'_',chosen_position,'_',IFNULL(rank,0)) PUB from dec_position_published ) a RIGHT JOIN ( SELECT textbook_id,declaration_id, CONCAT(textbook_id,'_',declaration_id,'_',preset_position,'_',chosen_position,'_',IFNULL(rank,0)) RES from dec_position WHERE chosen_position !=0 ) b on b.textbook_id = a.textbook_id and a.declaration_id = b.declaration_id ) T GROUP BY textbook_ID )f on f.textbook_id = a.id LEFT JOIN (SELECT textbook_id ,CONCAT(',',GROUP_CONCAT(CONCAT(textbook_id,'_',declaration_id,'_',preset_position,'_',chosen_position,'_',IFNULL(rank,0))),',') allres from dec_position_published GROUP BY textbook_id ) g on g.textbook_id = a.id where d.is_deleted =0 and d.is_published =1 and d.id = #{parameter.materialId}
and a.planning_editor = #{parameter.pmphUserId}
and a.is_locked = 0
and a.is_locked = 1
