SELECT dc.`org_name`,dp.`preset_position`,dp.`chosen_position`, wu.`rank`,dp.`textbook_id`,wu.`id` userId,wu.`realname`, wu.`is_writer`,org.`org_name` declarationUnit FROM `dec_position` dp LEFT JOIN `declaration` dc ON dp.`declaration_id` = dc.`id` LEFT JOIN `org` org ON dc.`org_id`= org.`id` LEFT JOIN `writer_user` wu ON wu.`id`= dc.`user_id` WHERE dc.is_staging=false and dp.chosen_position!=0
and dp.`textbook_id` =#{parameter.textBookId}
limit #{start},#{pageSize}

SELECT t.textbook_name,t.textbook_round,t.sort,dp.chosen_position,wu.realname,wu.username,org.org_name unitName, dc.org_name,dc.position,dp.rank,dc.title,dc.address,dc.email, dc.handphone,dc.idtype,dc.idcard,dc.online_progress,dc.offline_progress, m.material_name,dc.postcode,dc.birthday,pu.realname as editorName FROM `dec_position` dp LEFT JOIN `declaration` dc ON dp.`declaration_id` = dc.`id` LEFT JOIN `org` org ON dc.`org_id`= org.`id` LEFT JOIN `writer_user` wu ON wu.`id`= dc.`user_id` LEFT JOIN `textbook` t ON t.`id`= dp.`textbook_id` LEFT JOIN `material` m ON m.`id`= t.`material_id` LEFT JOIN pmph_user pu ON pu.id = t.planning_editor WHERE dc.is_staging=false and dc.online_progress!=0 and dp.chosen_position !=0 and dp.`textbook_id` in
#{item}
ORDER BY t.sort asc ,convert(t.textbook_name using gbk) asc,dp.chosen_position desc,dp.rank asc

select tk.textbook_name,tk.textbook_round,tk.sort, CONCAT('[',GROUP_CONCAT(CONCAT('{realname:"',dc.realname,'",', 'chosenOrgName:"',if(dc.org_id >0,org.org_name,'人民卫生出版社'),'",','chosenPosition:',dp.chosen_position,',', 'rank:',if(dp.rank is not null,dp.rank,0),'}') ORDER BY if(dp.chosen_position>8,dp.chosen_position-8,dp.chosen_position) desc ,dp.rank asc),']') writers from dec_position dp left join declaration dc on dp.declaration_id=dc.id left join org org on dc.org_id=org.id left join writer_user wu on wu.id =dc.user_id left join textbook tk on tk.id =dp.textbook_id left join material m on m.id=tk.material_id WHERE dc.is_staging=false and dc.online_progress!=0 and dp.chosen_position in(12,4,10,2) and dp.`textbook_id` in
#{item}
GROUP BY dp.`textbook_id`
