mapper.select:
SELECT w.realname,p.realname editorName,t.* FROM topic t LEFT JOIN writer_user w ON w.id = t.user_id LEFT JOIN pmph_user p ON p.id = t.editor_id WHERE t.auth_progress IN
#{authProgress}
AND t.bookname like concat('%',#{bookname},'%')
AND TO_DAYS(t.submit_time) >= TO_DAYS(#{submitTime1})
AND TO_DAYS(t.submit_time) <= TO_DAYS(#{submitTime2})
ORDER BY t.submit_time DESC limit #{start},#{pageSize}

SELECT count(*)
FROM topic a
LEFT JOIN
writer_user b ON a.user_id= b.id
LEFT JOIN pmph_user c ON a.editor_id =c.id
WHERE	
	a.auth_progress in

	#{authProgress}


	and a.is_director_handling =#{isDirectorHandling}


	and a.is_opts_handling = #{isOptsHandling}


	and a.is_editor_handling = #{isEditorHandling}


	AND a.bookname like concat('%',#{bookname},'%')


	AND a.submit_time like concat('%',#{submitTime},'%')


	and	a.editor_id=#{editorId}

ORDER BY a.submit_time

SELECT a.*,b.realname submitName,c.realname editorName
	FROM topic a
	LEFT JOIN
	writer_user b ON a.user_id= b.id
	LEFT JOIN pmph_user c ON a.editor_id =c.id
	WHERE 
		 a.auth_progress in
	
		#{authProgress}
	
	
		and a.is_director_handling =#{isDirectorHandling}
	
	
		and a.is_opts_handling = #{isOptsHandling}
	
	
		and a.is_editor_handling = #{isEditorHandling}
	
	
		AND a.bookname like concat('%',#{bookname},'%')
	
	
		AND a.submit_time like concat('%',#{submitTime},'%')
	
	
		and	a.editor_id=#{editorId}
	
	ORDER BY a.submit_time
	limit
	#{start},#{pageSize}
