mapper.select:
SELECT a.id,a.msg_id,a.msg_type,a.gmt_create sendTime,a.gmt_update reciveTime, a.is_read,a.receiver_type,a.is_withdraw,t.count, case when a.receiver_type =1 then b.username when a.receiver_type =2 then c.username when a.receiver_type =3 then '' end username, -- 用户名 case when a.receiver_type =1 then b.realname when a.receiver_type =2 then c.realname when a.receiver_type =3 then '' end name, -- 用户真实姓名 case when a.receiver_type =1 then '' when a.receiver_type =2 then '' when a.receiver_type =3 then d.username end orgusername, -- 机构账号 case when a.receiver_type =1 then '' when a.receiver_type =2 then '' when a.receiver_type =3 then d.org_name end dptname, -- 机构名称 case when a.receiver_type =1 then b.handphone when a.receiver_type =2 then c.handphone when a.receiver_type =3 then d.handphone end handphone from user_message a -- 社内用户 发送消息 查询部门名称 为机构名称 left join (select a.*,1 usertype from pmph_user a) b on b.id=a.receiver_id and b.usertype = a.receiver_type -- left join pmph_department bd on bd.id=b.department_id -- 给作家用户发送消息 查询其机构 left join (select a.*,2 usertype from writer_user a) c on c.id=a.receiver_id and c.usertype = a.receiver_type -- left join org cd on cd.id = c.org_id -- 给机构用户发送消息 查询其机构 left join (select a.*,3 usertype,o.org_name from org_user a LEFT JOIN org o on a.org_id = o.id) d on d.id=a.receiver_id and d.usertype = a.receiver_type -- left join org dd on dd.id = d.org_id, (SELECT count(*) count from user_message a -- 社内用户 发送消息 查询部门名称 为机构名称 left join (select a.*,1 usertype from pmph_user a) b on b.id=a.receiver_id and b.usertype = a.receiver_type -- left join pmph_department bd on bd.id=b.department_id -- 给作家用户发送消息 查询其机构 left join (select a.*,2 usertype from writer_user a) c on c.id=a.receiver_id and c.usertype = a.receiver_type -- left join org cd on cd.id = c.org_id -- 给机构用户发送消息 查询其机构 left join (select a.*,3 usertype,o.org_name from org_user a LEFT JOIN org o on a.org_id = o.id) d on d.id=a.receiver_id and d.usertype = a.receiver_type -- left join org dd on dd.id = d.org_id where is_withdraw =0
and is_read = #{parameter.isRead}
and sender_id = #{parameter.senderId}
and (bd.dp_name like concat('%',#{parameter.name},'%') or cd.org_name like concat('%',#{parameter.name},'%') or dd.org_name like concat('%',#{parameter.name},'%') or b.realname like concat('%',#{parameter.name},'%') or c.realname like concat('%',#{parameter.name},'%') or d.realname like concat('%',#{parameter.name},'%') )
and msg_id = #{parameter.msgId}
and material_id = #{parameter.materialId}
) t where is_withdraw =0
and is_read = #{parameter.isRead}
and sender_id = #{parameter.senderId}
and (bd.dp_name like concat('%',#{parameter.name},'%') or cd.org_name like concat('%',#{parameter.name},'%') or dd.org_name like concat('%',#{parameter.name},'%') or b.realname like concat('%',#{parameter.name},'%') or c.realname like concat('%',#{parameter.name},'%') or d.realname like concat('%',#{parameter.name},'%') )
and msg_id = #{parameter.msgId}
and material_id = #{parameter.materialId}
order by name
limit #{start},#{pageSize}

SELECT * from ( SELECT m.id, m.msg_id, m.title, m.sender_id, m.is_withdraw, m.`sender_type`, ( CASE WHEN m.`sender_type` = 0 ||m.`sender_type` = 1 THEN u.realname WHEN m.`sender_type` = 2 THEN wu.`realname` WHEN m.`sender_type` = 3 THEN ou.`realname` END ) sendName, m.gmt_create sendTime FROM user_message m LEFT JOIN pmph_user u ON u.id = m.sender_id left join writer_user wu ON wu.id = m.sender_id left join org_user ou ON ou.id = m.sender_id WHERE m.is_deleted = 0 AND m.msg_type = 1 and m.material_id = 0
AND m.sender_id in
#{userId}
and m.title like concat('%',#{parameter.title},'%')
GROUP BY m.msg_id, m.sender_id, m.title ORDER BY m.`gmt_create` DESC)t where t.sendName is not null LIMIT #{start},#{pageSize}

SELECT *, m.gmt_create sendTime FROM user_message m WHERE m.receiver_id=#{parameter.userId} AND m.receiver_type = #{parameter.userType} AND m.is_deleted = false AND m.is_withdraw = false
and m.title like concat('%',#{parameter.title},'%')
and m.is_read = #{parameter.isRead}
ORDER BY m.gmt_create DESC LIMIT #{start},#{pageSize}

mapper.update:
update user_message set is_deleted = true where id in
#{id}
