mapper.select:
SELECT id, username, password, is_disabled, org_id, realname, sex, experience, position, title, rank, is_teacher, cert, auth_time, auth_user_type, auth_user_id, is_writer, is_expert, avatar, signature, is_deleted FROM writer_user WHERE 1=1
and username = #{username}
and password = #{password}

select * from writer_user where
is_deleted =0 and org_id in
#{orgId}

select
count(*) from
writer_user ;

SELECT wu.`id`,wu.`realname`,wu.`username`,wu.`idcard`,o.`org_name`,wu.`handphone`, wu.`email`,wu.`position`,wu.`title`,wuc.`cert`,wu.address,wu.rank,wu.is_disabled,wuc.`progress`,t.count FROM `writer_user` wu LEFT JOIN `writer_user_certification` wuc ON wu.`id`= wuc.`user_id` LEFT JOIN `org` o ON wuc.`org_id` = o.`id`, ( SELECT count(*) `count` FROM `writer_user` wu LEFT JOIN `writer_user_certification` wuc ON wu.`id`= wuc.`user_id` LEFT JOIN `org` o ON wuc.`org_id` = o.`id` where wu.`is_deleted`=0 and wuc.`progress` !=0
and o.org_name like concat('%',#{parameter.orgName},'%')
and ( wu.username like concat('%',#{parameter.realname},'%' ) or wu.realname like concat('%',#{parameter.realname},'%' ) )
and wu.rank = #{parameter.rank}
and wuc.`progress` = #{parameter.progress}
)t WHERE wu.`is_deleted`=0 and wuc.`progress` !=0
and o.org_name like concat('%',#{parameter.orgName},'%')
and ( wu.username like concat('%',#{parameter.realname},'%' ) or wu.realname like concat('%',#{parameter.realname},'%' ) )
and wu.rank = #{parameter.rank}
and wuc.`progress` = #{parameter.progress}
order by wu.sort
limit #{start},#{pageSize}

SELECT a.id,e.id,a.realname,a.username,d.chosen_position,a.org_id, b.org_name FROM writer_user a LEFT JOIN org b ON a.org_id = b.id LEFT JOIN declaration c ON a.id = c.user_id LEFT JOIN dec_position d ON c.id = d.declaration_id LEFT JOIN textbook e ON d.textbook_id = e.id WHERE 1=1
and e.textbook_name like concat('%',#{parameter.bookname},'%')
and d.chosen_position = #{parameter.chosenPosition}
and (a.username like concat('%',#{parameter.name},'%' ) or a.realname like concat('%',#{parameter.name},'%' ) )
limit #{start},#{pageSize}

